import "core-js/modules/es6.number.constructor";
export default {
  name: 'validCode',
  props: {
    width: {
      type: String,
      default: '100px'
    },
    height: {
      type: String,
      default: '40px'
    },
    length: {
      type: Number,
      default: 4
    }
  },
  data: function data() {
    return {
      codeList: []
    };
  },
  mounted: function mounted() {
    this.createdCode();
  },
  methods: {
    refreshCode: function refreshCode() {
      this.createdCode();
    },
    createdCode: function createdCode() {
      var len = this.length,
        codeList = [],
        chars = 'ABCDEFGHJKMNPQRSTWXYZabcdefhijkmnprstwxyz0123456789',
        charsLen = chars.length;
      // 生成
      for (var i = 0; i < len; i++) {
        var rgb = [Math.round(Math.random() * 220), Math.round(Math.random() * 240), Math.round(Math.random() * 200)];
        codeList.push({
          code: chars.charAt(Math.floor(Math.random() * charsLen)),
          color: "rgb(".concat(rgb, ")"),
          fontSize: "20px",
          padding: "2px",
          transform: "rotate(0deg)"
        });
      }
      // 指向
      this.codeList = codeList;
      // 将当前数据派发出去
      this.$emit('update:value', codeList.map(function (item) {
        return item.code;
      }).join(''));
    },
    getStyle: function getStyle(data) {
      return "color: ".concat(data.color, "; font-size: ").concat(data.fontSize, "; padding: ").concat(data.padding, "; transform: ").concat(data.transform);
    }
  }
};
