export default {
  name: "Review",
  data() {
    return {
        query: {},
        dialogName: '', 
        tableData: [],    // 모든 데이터
        currentPage: 1,   // 현재 페이지
        pagesize: 10,     // 페이지당 데이터 개수
        totalCount: 0,    // 총 데이터 수
        imageUrl: null,
        fileUrl: null,
        dialogVisible: false,
        form: {},
        rules: {
          gid: [
              { required: true, message: '상품을 입력하세요', trigger: 'blur' },
          ],
          uid: [
              { required: true, message: '사용자를 입력하세요', trigger: 'blur' },
          ],
          content: [
              { required: true, message: '댓글 내용을 입력하세요', trigger: 'blur' },
          ],
        },
        type:'',
        userInfo:{},
    };
  },
  created() {
      this.type = this.common.get('type');
      this.userInfo = this.common.getUserInfo('userInfo');
      if(this.userInfo===null){
         this.$router.push('/');
      }
      this.getData();
  },
  methods: {
    // 페이지당 데이터 개수 변경
    handleSizeChange: function(val) {
        this.pagesize = val;
        this.getData();
    },
    // 페이지 변경
    handleCurrentChange: function(val) {
        this.currentPage = val;
        this.getData();
    },
    // 데이터 가져오기
    getData() {
        var param = {
            goodsName: this.query.goodsName,
            uid: this.type==='02'?this.userInfo.id:'',
            pagesize: this.pagesize,
            currentPage: this.currentPage,
        };
        this.$axios.post('/api/review/selectPage',param).then(res => {
            if(res.data.code == 200){
                this.tableData = res.data.data.list;
                this.totalCount = res.data.data.total;
            } else {
                this.$message.warning(res.data.msg);
            }
        });
    },
    // 편집
    handleEdit(index, row) {
        this.form = JSON.parse(JSON.stringify(row));
        this.dialogVisible = true;
        this.$refs['ruleForm'].clearValidate();
        this.dialogName = "상품 댓글 편집";
    },
    // 추가
    handleAdd() {
        if (this.$refs.rulform !== undefined) this.$refs.rulform.resetFields();
        this.dialogVisible = true;
        this.form = {};
        this.$refs['ruleForm'].clearValidate();
        this.dialogName = "상품 댓글 추가";
    },
    // 저장
    save(formName) {
        this.$refs[formName].validate((valid) => {
          if (valid) {
            this.$axios.post(this.form.id?'/api/review/edit' : '/api/review/add', this.form).then(res => {
              if(res.data.code == 200){
                this.$message.success(res.data.msg);
                this.dialogVisible = false;
                this.getData();
              } else {
                this.$message.warning(res.data.msg);
              }
            })
          } else {
            console.log('error submit!!');
            return false;
          }
        });
    },
    // 삭제
    handleDelete(index, row) {
        this.$confirm('정말 삭제하시겠습니까?', '알림', {
           type: 'warning'
        }).then(() => {
             this.$axios.get('/api/review/deleteById?id='+row.id).then(res => {
                if(res.data.code == 200){
                    this.$message.success(res.data.msg);
                    this.tableData.splice(index, 1);
                    this.getData();
            }else{
              this.$message.warning(res.data.msg);
            }
          });
        }).catch(() => {});
    },
  }
}
